function appendComponent(parent, child, childProperty = null, closeTag){
  var html = null
  var property = ""
  var openTag = `${child}`

  $.each(childProperty, function(key, element) {
    property += ` ${key}="${element}"`
  })

  if(property != "") openTag = `${child}${property}`

  if(closeTag) html = `<${openTag}>1</${child}>`
  else html = `<${openTag}>`

  parent.append(html)
}

function initItem(){

}

function initLayout(callback){
  var app = $("section#app")

  appendComponent(app, "div", {
    "class": "container"
  }, true)
}

